import React, { useState } from 'react';
import './Profile.css';
import { useAuth, AuthContext } from './auth/Auth';
import Axios from 'axios';

const Profile = () => {

    const submitProfile = () => {
        Axios.put(`http://localhost:5000/api/user/update/${dataUser.id}`)
        .then(response => response.data)
    }

	const dataUser = React.useContext(AuthContext);
	const defaultAvatar = 'https://image.freepik.com/vector-gratis/payaso-aterrador-sombrero-rojo_43623-432.jpg';

	return (
		<div className="Profile_container">
			<div className="Profile_input_container">
				<img
					className="Profile_user_img"
					src={(dataUser && dataUser.userInfo && dataUser.userInfo.avatar) || defaultAvatar}
				/>
				<input name="firstname" className="Profile_username" type="text" placeholder="PrÃ©nom" />
				<input name="lastname" className="Profile_username" type="text" placeholder="Nom" />
				<input name="pseudo" className="Profile_username" type="text" placeholder="Pseudo" />
				<input name="password" className="Profile_password" type="password" placeholder="Mot de passe" />

				<div className="Profile_container_button">
					<button className="Profile_button">Enregistrer</button>
				</div>
			</div>
		</div>
	);
};

export default Profile;
