import React, { useState } from "react"
import './BurgerMenu.css'

const BurgerMenu = () => {

    const [ displayBurger, setDisplayBurger ] = useState({
        open: false
    })

    function displayAside(){
        const x = document.getElementsByClassName("BurgerMenu_container");
        (x.style.width === "20vw") ? x.style.width = "-200px" : x.style.width= "20vw";
    }

    return(
        <div className="BurgerMenu_container">
            <ul className="BurgerMenu_menu">
                <li className="BurgerMenu_user">
                    {/* <div className="BurgerMenu_user_img"></div>
                    <h3 className="BurgerMenu_user_name">FuenCore</h3> */}
                </li>
                <li className="BurgerMenu_navlink_event">Les tourn√©es</li>
                <li className="BurgerMenu_navlink_artist">Les artistes</li>
                <li className="BurgerMenu_navlink_history">Notre histoire</li>
                <div className="BurgerMenu_condition_container">
                <li className="BurgerMenu_condition">A propos</li>
                <li className="BurgerMenu_condition">Nous contacter</li>
                </div>
            </ul>
            <button className="BurgerMenu_button" onClick={displayAside}>></button>
        </div>
    )
}

export default BurgerMenu;
