import React, { useState, useEffect } from "react";
import "./ChatPage.css";
import Axios from "axios";

const ChatPage = () => {

    const [ messages, setMessage ] = useState({})

    useEffect(() => {
        Axios.get('http://localhost:5000/api/chat')
        .then(response => response.data)
        .then(data => setMessage(data))
    }, [])

    console.log(messages)

    return(
        <div className="ChatPage_container">
            <div className="ChatPage_read">
                Nous sommes des messages
            </div>
            <div className="ChatPage_write">
                <textarea type="text" className="ChatPage_textarea"/>
                <button type="sumbit" className="ChatPage_sendbutton"/>
            </div>
        </div>
    )
}

export default ChatPage;
