import React, { useState } from 'react';
import { Redirect } from 'react-router';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { useAuth, AuthContext } from './Auth';
import BgGlitch from '../BgGlitch';
import './SignUp.css';

const SignUp = () => {
	const userHook = React.useContext(AuthContext);
	const [ isLoggedIn, setIsLoggedIn ] = useState(false);
	const [ username, setUsername ] = useState('');
	const [ pass, setPass ] = useState('');
	const { setAuthTokens } = useAuth();
	const postSignUp = () => {
		axios
			.post(`http://localhost:5000/api/auth/login`, {
				username,
				pass
			})
			.then((res) => {
				//  userHook.setAuthTokens(res.data.token);
				setAuthTokens(res.data.token);
				userHook.setUserInfo(res.data.user[0]);
				setIsLoggedIn(true);
			});
	};

	if (isLoggedIn) {
		return <Redirect to="/Main" />;
	}

	return (
		<div className="SignUp_container">

			<div className="SignUp_BgGlitch">
				<BgGlitch title="MADE BY FUENCORE" glitch="WILD_CIRCUS"/>
			</div>	
			<div className="SignUp_input_container">
			<div className="SignUp_container_title">
				<h3 className="SignUp_title">INSCRIT-T0I ! !</h3>
			</div>	
				<input
					name="username"
					className="SignUp_username"
					type="username"
					placeholder="Nom de compte"
					onChange={(e) => setUsername(e.target.value)}
				/>
				<input
					name="password"
					className="SignUp_password"
					type="password"
					placeholder="Mot de passe"
					onChange={(e) => setPass(e.target.value)}
				/>
				<div className="SignUp_container_button">

					<Link to="/signup">
					<button className="SignUp_button">
						S'inscrire
					</button>
					</Link>
					<Link to="/main">
						<button className="SignUp_button_skip">Passer</button>
					</Link>
				</div>
			</div>
		</div>
	);
};

export default SignUp;
