import React, { useEffect, useState } from 'react';
import { storage } from '../../firebase/Config';
import { AuthContext } from '../auth/Auth';
import './AddPlace.css';
import BgGlitch from '../BgGlitch';
import Axios from 'axios';

const AddPlace = () => {
	const dataUser = React.useContext(AuthContext);
	const [ isSubmit, setIsSubmit ] = useState(false);
	const [ avatar, setAvatar ] = useState({
		name: null,
		url: null
	});

	const submitAvatar = () => {
		const uploadTask = storage.ref(`images/${avatar.name}`).put(avatar);
		uploadTask.on(
			'state_changed',
			(snapshop) => {
				// progress function
			},
			(error) => {
				console.log('error', error);
			},
			() => {
				// complete function ...
				storage.ref('images').child(avatar.name).getDownloadURL().then((url) => {
					setAvatar({ url: url });
					Axios.put(`http://localhost:5000/api/exploit`, {
						avatar: url
					}).then(setIsSubmit(true));
				});
			}
		);
	};

	useEffect(() => {
		Axios.get('http://localhost:5000/api/exploit').then((res) => res.data).then((data) => setPlace(data));
	}, []);

	const [ place, setPlace ] = useState([]);
	const [ newPlace, setNewPlace ] = useState({
		name: '',
		place: '',
		image: ''
	});

	return (
		<div className="AddPlace_container">
			<div className="Login_BgGlitch">
				<BgGlitch title="MADE BY FUENCORE" glitch="WILD_CIRCUS" />
			</div>
			<div className="AddPlace_box">
				<ul className="AddPlace_ul">
					{place.map((x) => (
						<li className="AddPlace_li">
							<button id="AddPlace_button" type="submit">
								Remove
							</button>
							<img className="AddPlace_img" src={x.image} key={x.id} />
							<div className="AddPlace_info_container">
								<p className="AddPlace_name" key={x.id}>
									{x.name}
								</p>
								<p className="AddPlace_place" key={x.id}>
									{x.place}
								</p>
							</div>
						</li>
					))}
				</ul>
				<div className="AddPlace_box2">
					<li className="AddPlace_li">
						<div id="AddPlace_button_image" class="upload-btn-wrapper">
							<button class="btn"> IMAGE</button>
							<input
								type="file"
								name="name"
								accept="image/jpeg, image/png"
								onChange={(e) => setAvatar(e.target.files[0])}
							/>
						</div>{' '}
						<img className="AddPlace_img" src={newPlace.image} />
						<div className="AddPlace_info_container">
							<input
								type="text"
								className="AddPlace_name_1"
								placeholder="Entrer un nom"
								onChange={(e) => setNewPlace({ name: e.target.value })}
							/>
							<input
								type="text"
								className="AddPlace_place_1"
								placeholder="Entrer un lieu"
								onChange={(e) => setNewPlace({ place: e.target.value })}
							/>
						</div>
					</li>
				</div>
				<button type="submit">Enregistrer les modifications</button>
			</div>
		</div>
	);
};

export default AddPlace;
