import React, { useEffect, useState } from 'react';
import './AddPlace.css';
import BgGlitch from "../BgGlitch"
import Axios from 'axios';

const AddPlace = () => {

	useEffect(() => {
		Axios.get('http://localhost:5000/api/exploit').then((res) => res.data).then((data) => setPlace(data));
	}, []);

    const [ place, setPlace ] = useState([]);
    const [ newPlace, setNewPlace ] = useState({
        name: "",
        place: "",
        image: ""
    })


	return (
		<div className="AddPlace_container">
            <div className="Login_BgGlitch">
				<BgGlitch title="MADE BY FUENCORE" glitch="WILD_CIRCUS"/>
			</div>	
			<div className="AddPlace_box">
				<ul className="AddPlace_ul">
					{place.map(x => (
						<li className="AddPlace_li">
                            <button id="AddPlace_button"type="submit">Remove</button>
							<img className="AddPlace_img" src={x.image} key={x.id}/>
                            <div className="AddPlace_info_container">
							<p className="AddPlace_name" key={x.id}>{x.name}</p>
							<p className="AddPlace_place" key={x.id}>{x.place}</p>
                            </div>
						</li>
					))}
				</ul>
                <div className="AddPlace_box2">
                <li className="AddPlace_li">
                            <button id="AddPlace_button"type="submit">Remove</button>
							<img className="AddPlace_img" src={newPlace.image} />
                            <div className="AddPlace_info_container">
							<input type="text" className="AddPlace_name" text="Entrer un nom"/>
							<input type="text" className="AddPlace_place" text="Entrer un lieu"/>
                            </div>
						</li>
                </div>
			</div>
		</div>
	);
};

export default AddPlace;
