import React, { useState } from 'react';
import { DefaultButton } from '../shapes/Shapes';
import { Redirect } from 'react-router';
import { connect } from 'react-redux';
import axios from 'axios';
import { useAuth } from './Auth';
import './Login.css';

const Login = ({ dispatch }, props) => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isError, setIsError] = useState(false);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const { setAuthTokens } = useAuth();

  const postLogin = () => {
    axios
      .post(`${process.env.REACT_APP_AXIOS_URL}/api/auth/login`, {
        email,
        password
      })
      .then(res => {
        setAuthTokens(res.data.token);
        setIsLoggedIn(true);
        dispatch({
          type: 'GET_LICENCE',
          payload: res.data.user[0].licence_nbr
        });
        dispatch({
          type: 'GET_IS_ADMIN',
          payload: res.data.user[0].is_admin
        });
        dispatch({
          type: 'GET_IS_SUPER',
          payload: res.data.user[0].is_super
        });

        setIsError(true);
      })
      .catch(e => {
        setIsError(true);
      });
  };

  if (isLoggedIn) {
    return <Redirect to="/Home" />;
  }
  return (
    <div className="loginForm-shape">
      <div className="loginForm-logo">FC</div>

      <div className="login-spread">
        <div className="loginForm-form">
          <input
            name="email"
            className="loginForm-input"
            type="email"
            placeholder="Email"
            onChange={e => {
              setEmail(e.target.value);
            }}
          />
          <br />
          <input
            name="password"
            className="loginForm-input"
            type="password"
            placeholder="Mot de passe"
            onChange={e => {
              setPassword(e.target.value);
            }}
          />
          <br />
          <DefaultButton addClass="loginForm-btn" text="Connexion" addOnClick={() => postLogin()} />
        </div>
        <div>
        </div>
        <div className="subscribe-shape">
          <p className="subscribe-link">Premi√®re connexion ?</p>
          <button
            className="firstCo-btn"
            type="button"
            rootLink="/Subscribe"
            text="S'inscrire"
          />
        </div>
      </div>
    </div>
  );
};
// }
const mapStateToProps = store => {
  return { url: store.todos.url };
};

export default connect(mapStateToProps, null)(Login);
